curl -X POST http://localhost:3000/auth/register \
  -H "Content-Type: application/json" \
  -d '{
    "email": "example@gmail.com",
    "username": "john",
    "password": "securepassword"
  }'

curl -X POST http://localhost:3000/auth/login \
  -H "Content-Type: application/json" \
  -d '{
    "email": "example@gmail.com",
    "password": "securepassword"
  }'

curl -X POST http://localhost:3000/auth/verify \
  -H "Content-Type: application/json" \
  -d '{
    "email": "example@gmail.com",
    "otp": "000000"
  }'

curl -X POST http://localhost:3000/event/create \
  -H "Authorization: Bearer abc..." \
  -H "Content-Type: application/json" \
  -d '{
    "name": "Tech Conference 2025",
    "description": "An annual conference bringing together tech enthusiasts and professionals.",
    "location": "San Francisco, CA",
    "startDate": "2025-05-10T09:00:00",
    "endDate": "2025-05-12T17:00:00",
    "image": ""
  }'

curl -X POST http://localhost:3000/ticket/create \
-H "Authorization: Bearer abc..." \
