-d '{
    "event_id": 2
}'

curl -X GET http://localhost:3000/ticket \
-H "Authorization: Bearer abc..."

curl -X GET http://localhost:3000/event \
-H "Authorization: Bearer abc..."

curl -X GET http://localhost:3000/ticket/1 \
